import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
	const [mode, setMode] = useState(null);
	const [fileList, setFileList] = useState([]);
	const [selectedFile, setSelectedFile] = useState(null);
	const [error, setError] = useState(null);
	const [data, setData] = useState({});
	// New state for testing mode:
	const [userAnswer, setUserAnswer] = useState("");
	const [feedback, setFeedback] = useState("");
	const [testItem, setTestItem] = useState(null);

	useEffect(() => {
		if (mode) {
			axios.get('/list-jsons')
				.then(response => {
					setFileList(response.data);
					setError(null);
				})
				.catch(() => {
					setError('Failed to fetch file list');
				});
		}
	}, [mode]);

	useEffect(() => {
		fetch('/api/data')
			.then(res => res.json())
			.then(setData)
			.catch(console.error);
	}, []);

	const handleModeChange = (newMode) => {
		setMode(newMode);
		setSelectedFile(null);
		setFileList([]);
	};

	const handleFileClick = (fileId) => {
		axios.get(`/json/${fileId}`)
			.then(response => {
				const parsed = response.data;
				setSelectedFile({ name: fileId, content: JSON.stringify(parsed, null, 2), raw: parsed });
				setError(null);
				// For testing mode, pick a random word and reset answer/feedback
				if (mode === "testing" && Array.isArray(parsed) && parsed.length > 0) {
					const randomWord = parsed[Math.floor(Math.random() * parsed.length)];
					setTestItem(randomWord);
					setUserAnswer("");
					setFeedback("");
				} else {
					setTestItem(null);
				}
			})
			.catch(() => {
				setError('Failed to fetch file content');
			});
	};

	// New function to check answer in testing mode
	const handleSubmit = () => {
		if (!testItem) return;
		if (userAnswer.trim().toLowerCase() === testItem.german.toLowerCase()) {
			setFeedback("Correct!");
		} else {
			setFeedback(`Incorrect! Correct answer: ${testItem.german}`);
		}
	};

	return (
		<div style={{ padding: '20px' }}>
			{!mode ? (
				<div>
					<button onClick={() => handleModeChange('learning')}>Learning</button>
					<button onClick={() => handleModeChange('testing')}>Testing</button>
				</div>
			) : (
				<div>
					<h2>{mode.charAt(0).toUpperCase() + mode.slice(1)} Mode</h2>
					<div>
						{fileList.map((fileId, idx) => (
							<button key={idx} onClick={() => handleFileClick(fileId)}>
								{fileId}
							</button>
						))}
					</div>
					{error && <div style={{ color: 'red' }}>{error}</div>}
					{selectedFile && (
						<div style={{ marginTop: '20px' }}>
							<h3>{selectedFile.name}</h3>
								{mode === 'learning' && selectedFile.raw && Array.isArray(selectedFile.raw) ? (
									<table style={{ width: '100%', borderCollapse: 'collapse', fontSize: '18px' }}>
										<thead>
											<tr style={{ background: '#ddd' }}>
												{Object.keys(selectedFile.raw[0]).map((key) => (
													<th key={key} style={{ border: '1px solid #000', padding: '8px', textTransform: 'capitalize' }}>
														{key}
													</th>
												))}
											</tr>
										</thead>
										<tbody>
											{selectedFile.raw.map((item, index) => (
												<tr key={index} style={{ background: index % 2 === 0 ? '#ffffff' : '#f0f0f0' }}>
													{Object.values(item).map((value, idx) => (
														<td key={idx} style={{ border: '1px solid #000', padding: '8px' }}>
															{value}
														</td>
													))}
												</tr>
											))}
										</tbody>
									</table>
								) : mode === 'testing' ? (
									testItem ? (
										<div>
											<p style={{ fontSize: '18px' }}>{testItem.french} - </p>
											<input
												type="text"
												value={userAnswer}
												onChange={(e) => setUserAnswer(e.target.value)}
												placeholder="Type German word"
												style={{ fontSize: '18px', padding: '5px' }}
											/>
											<button onClick={handleSubmit} style={{ marginLeft: '10px', padding: '5px 10px' }}>Submit</button>
											{feedback && (
												<p style={{ marginTop: '10px', color: feedback === "Correct!" ? 'green' : 'red' }}>{feedback}</p>
											)}
										</div>
									) : (
										<p>No test item available.</p>
									)
								) : (
									<pre style={{ background: '#f4f4f4', padding: '10px', fontSize: '18px' }}>
										{selectedFile.content}
									</pre>
									)}
						</div>
					)}
				</div>
				)}
				<h1>Vocabulary JSON Data</h1>
				<div style={{ display: 'flex', flexWrap: 'wrap' }}>
					{Object.keys(data).map(fileName => (
						<div key={fileName} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px', flex: '1 0 300px' }}>
							<h2>{fileName}</h2>
							<ul>
								{Array.isArray(data[fileName])
									? data[fileName].map((item, index) => (
										<li key={index}>
											{item.french} - {item.german}
										</li>
									))
									: <li>{JSON.stringify(data[fileName])}</li>}
							</ul>
						</div>
					))}
				</div>
		</div>
	);
}

export default App;
