import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
	const [mode, setMode] = useState(null);
	const [fileList, setFileList] = useState([]);
	const [selectedFile, setSelectedFile] = useState(null);
	const [error, setError] = useState(null);

	useEffect(() => {
		if (mode) {
			axios.get('/list-jsons')
				.then(response => {
					setFileList(response.data);
					setError(null);
				})
				.catch(() => {
					setError('Failed to fetch file list');
				});
		}
	}, [mode]);

	const handleModeChange = (newMode) => {
		setMode(newMode);
		setSelectedFile(null);
		setFileList([]);
	};

	const handleFileClick = (fileId) => {
		axios.get(`/json/${fileId}`)
			.then(response => {
				setSelectedFile({ name: fileId, content: JSON.stringify(response.data, null, 2), raw: response.data });
				setError(null);
			})
			.catch(() => {
				setError('Failed to fetch file content');
			});
	};

	return (
		<div style={{ padding: '20px' }}>
			{!mode ? (
				<div>
					<button onClick={() => handleModeChange('learning')}>Learning</button>
					<button onClick={() => handleModeChange('testing')}>Testing</button>
				</div>
			) : (
				<div>
					<h2>{mode.charAt(0).toUpperCase() + mode.slice(1)} Mode</h2>
					<div>
						{fileList.map((fileId, idx) => (
							<button key={idx} onClick={() => handleFileClick(fileId)}>
								{fileId}
							</button>
						))}
					</div>
					{error && <div style={{ color: 'red' }}>{error}</div>}
					{selectedFile && (
						<div style={{ marginTop: '20px' }}>
							<h3>{selectedFile.name}</h3>
							{ mode === 'learning' && selectedFile.name === "json1" ? (
								<table style={{ width: '100%', borderCollapse: 'collapse' }}>
									<thead>
										<tr style={{ background: '#ddd' }}>
											<th style={{ border: '1px solid #000' }}>French</th>
											<th style={{ border: '1px solid #000' }}>German</th>
										</tr>
									</thead>
									<tbody>
										{selectedFile.raw.map((item, index) => (
											<tr key={index} style={{ background: index % 2 === 0 ? '#ffffff' : '#f0f0f0' }}>
												<td style={{ border: '1px solid #000' }}>{item.french}</td>
												<td style={{ border: '1px solid #000' }}>{item.german}</td>
											</tr>
										))}
									</tbody>
								</table>
							) : (
								<pre style={{ background: '#f4f4f4', padding: '10px' }}>
									{selectedFile.content}
								</pre>
							)}
							{mode === 'testing' && (
								<div style={{ marginTop: '10px', color: 'green' }}>
									Test passed!
								</div>
							)}
						</div>
					)}
				</div>
			)}
		</div>
	);
}

export default App;
